    15	        '/\$_GET\s*\[\s*[\'"][^\'"]*[\'"]\s*\]\s*(?!.*esc_|.*sanitize_)/',
    16	        '/\$_POST\s*\[\s*[\'"][^\'"]*[\'"]\s*\]\s*(?!.*esc_|.*sanitize_)/',
    17	        '/\$wpdb->query\s*\(\s*[\'"].*\$.*[\'"]\s*\)/',
    18	        '/\$wpdb->prepare\s*\(\s*[\'"].*%s.*[\'"]\s*,\s*\$_/',
