    32	            'query-security-scanner',
    33	            [$this, 'admin_page']
    34	        );
    35	    }
    36	
    37	    public function scan_plugins() {
    38	        $results = [];
    39	        $plugins_dir = WP_PLUGIN_DIR;
    40	        
    41	        foreach (glob($plugins_dir . '/*/*.php') as $file) {
    42	            $issues = $this->scan_file($file);
    43	            if (!empty($issues)) {
    44	                $results[basename(dirname($file))][] = [
    45	                    'file' => str_replace($plugins_dir, '', $file),
    46	                    'issues' => $issues
    47	                ];
    48	            }
    49	        }
    50	        
    51	        wp_send_json_success($results);
    52	    }
    53	
    54	    private function scan_file($file_path) {
    55	        $content = file_get_contents($file_path);
    56	        $issues = [];
    57	        
    58	        foreach ($this->unsafe_patterns as $pattern) {
    59	            if (preg_match_all($pattern, $content, $matches, PREG_OFFSET_CAPTURE)) {
    60	                foreach ($matches[0] as $match) {
    61	                    $line_number = substr_count(substr($content, 0, $match[1]), "\n") + 1;
    62	                    $issues[] = [
    63	                        'type' => 'SQL Injection Risk',
    64	                        'line' => $line_number,
    65	                        'code' => trim($match[0])
    66	                    ];
    67	                }
    68	            }
    69	        }
    70	        
    71	        return $issues;
    72	    }
